{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyMzk4YmXc5uRHqDnoGjvTvU"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":null,"metadata":{"id":"SubRoz1WN7Dc"},"outputs":[],"source":["# -*- coding: utf-8 -*-\n","\"\"\"\n","ModTagCNN architecture for Keras.\n","\n","Reference:\n","- [Deep Convolutional Networks for Music Tagging](https://arxiv.org/abs/1606.00298)\n","- [Music-auto_tagging-keras](https://github.com/keunwoochoi/music-auto_tagging-keras)\n","\"\"\"\n","\n","from __future__ import print_function\n","import keras.backend as K\n","from keras.layers import Input, Dense, Dropout, Flatten, Conv2D, MaxPooling2D, BatchNormalization, ELU\n","from keras.models import Model\n","\n","def remove_layer(model):\n","    if not model.layers:\n","        raise Exception('The model is already empty.')\n","\n","    model.layers.pop()\n","    if not model.layers:\n","        model.outputs = []\n","    else:\n","        model.layers[-1].outbound_nodes = []\n","        model.outputs = [model.layers[-1].output]\n","    model.built = False\n","\n","def ModTagCNN(weighting='msd', input_tensor=None):\n","    \"\"\"\n","    Create the ModTagCNN architecture, and optionally load weights pre-trained on MSD.\n","\n","    # Arguments\n","        weighting: 'None' for random initialization, or 'msd' for pre-training on MSD.\n","        input_tensor: optional input tensor to use as the image input for the model.\n","\n","    # Returns\n","        A Keras model instance.\n","    \"\"\"\n","    if weighting not in {'msd', None}:\n","        raise ValueError('The `weighting` argument should be either `None` or `msd`.')\n","\n","    # Define input shape\n","    input_shape = (96, 1366, 1) if K.image_data_format() == 'channels_last' else (1, 96, 1366)\n","    if input_tensor is None:\n","        audio_input = Input(shape=input_shape)\n","    else:\n","        audio_input = Input(tensor=input_tensor, shape=input_shape)\n","\n","    # Define axis\n","    channel_axis = 3 if K.image_data_format() == 'channels_last' else 1\n","\n","    # Input block\n","    y = BatchNormalization(axis=channel_axis, name='bn_0_freq')(audio_input)\n","\n","    # Conv block 1\n","    y = Conv2D(32, (3, 3), padding='same', name='conv1')(y)\n","    y = BatchNormalization(axis=channel_axis, name='bn1')(y)\n","    y = ELU()(y)\n","    y = MaxPooling2D(pool_size=(2, 4), name='pool1')(y)\n","\n","    # Conv block 2\n","    y = Conv2D(128, (3, 3), padding='same', name='conv2')(y)\n","    y = BatchNormalization(axis=channel_axis, name='bn2')(y)\n","    y = ELU()(y)\n","    y = MaxPooling2D(pool_size=(2, 4), name='pool2')(y)\n","\n","    # Conv block 3\n","    y = Conv2D(128, (3, 3), padding='same', name='conv3')(y)\n","    y = BatchNormalization(axis=channel_axis, name='bn3')(y)\n","    y = ELU()(y)\n","    y = MaxPooling2D(pool_size=(2, 4), name='pool3')(y)\n","\n","    # Conv block 4\n","    y = Conv2D(192, (3, 3), padding='same', name='conv4')(y)\n","    y = BatchNormalization(axis=channel_axis, name='bn4')(y)\n","    y = ELU()(y)\n","    y = MaxPooling2D(pool_size=(3, 5), name='pool4')(y)\n","\n","    # Conv block 5\n","    y = Conv2D(256, (3, 3), padding='same', name='conv5')(y)\n","    y = BatchNormalization(axis=channel_axis, name='bn5')(y)\n","    y = ELU()(y)\n","    y = MaxPooling2D(pool_size=(4, 4), name='pool5')(y)\n","\n","    # Output\n","    y = Flatten(name='flatten')(y)\n","    if weighting is None:\n","        # Random initialization\n","        y = Dense(10, activation='sigmoid', name='output')(y)\n","        model = Model(audio_input, y)\n","        return model\n","    else:\n","        # Pre-trained on MSD\n","        y = Dense(50, activation='sigmoid', name='output')(y)\n","        if K.image_data_format() == 'channels_last':\n","            raise RuntimeError(\"Please set image_data_format == 'channels_first'. You can set it in your Keras config file.\")\n","        initial_model = Model(audio_input, y)\n","        initial_model.load_weights('weights/music_tagger_cnn_weights_%s.h5' % K.backend(), by_name=True)\n","\n","        # Replace last layer\n","        remove_layer(initial_model)\n","        last = initial_model.get_layer('flatten')\n","        preds = Dense(10, activation='sigmoid', name='predictions')(last.output)\n","        model = Model(initial_model.input, preds)\n","        return model\n"]},{"cell_type":"code","source":[],"metadata":{"id":"_q44HroEeAwN"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["from google.colab import drive\n","drive.mount('/content/drive')\n"],"metadata":{"colab":{"base_uri":"https://localhost:8080/"},"id":"rdzn4AWqeA-c","executionInfo":{"status":"ok","timestamp":1698415856534,"user_tz":-330,"elapsed":49952,"user":{"displayName":"Gaurav Ghop","userId":"17071669418159100136"}},"outputId":"690e3551-78d1-41a4-9d18-d170617e0481"},"execution_count":1,"outputs":[{"output_type":"stream","name":"stdout","text":["Mounted at /content/drive\n"]}]}]}